SELECT 'counts.template_repositories' AS counter_name,  COUNT(projects.id) AS counter_value, TO_DATE(CURRENT_DATE) AS run_day,   FROM {{ref('gitlab_dotcom_projects_dedupe_source')}} AS projects WHERE projects.namespace_id IS NULLSELECT COUNT(projects.id) FROM {{ref('gitlab_dotcom_projects_dedupe_source')}} AS projects INNER JOIN {{ref('gitlab_dotcom_namespaces_dedupe_source')}} AS namespaces ON projects.namespace_id = namespaces.custom_project_templates_group_id