SELECT namespace_id, TO_DATE(CURRENT_DATE) AS run_day,  COUNT(namespaces.id) AS counter_value  FROM {{ref('gitlab_dotcom_namespaces_dedupe_source')}} AS namespaces  LEFT JOIN {{ref('gitlab_dotcom_projects_dedupe_source')}} AS projects ON projects.id = namespaces.file_template_project_id  LEFT JOIN {{ref('gitlab_dotcom_namespaces_dedupe_source')}} AS namespaces ON projects.namespace_id = namespaces.id WHERE namespaces.type = 'Group' GROUP BY 1